import matplotlib.pyplot as plt
import numpy as np


def plot_list(list_series, list_names):
    fig = plt.figure()
    ax = plt.subplot(111)

    for i in range(len(list_series)):
        ax.plot(range(len(list_series[i])), list_series[i], label=list_names[i])

    ax.set_ylabel('fscore')
    ax.set_xlabel('round')

    ax.legend(loc=4)

    plt.show()


